using System;
using System.Collections.Generic;
using System.IO;
using Xamarin.Forms;

namespace Heleus.Apps.Shared
{
    public enum AppInfoType
    {
        Heleus,
        Message,
        Status,
        Todo,
        Verify,
        Note,
    }

    public class AppInfo
    {
        static readonly Dictionary<AppInfoType, AppInfo> _appInfo = new Dictionary<AppInfoType, AppInfo>();

        public readonly string Name;
        public readonly string Description;
        public readonly Uri Link;
        public readonly byte[] Image;
        public ImageSource ImageSource => ImageSource.FromStream(() => new MemoryStream(Image));

        AppInfo(string translation, string base64Image)
        {
            Name = Tr.Get($"{translation}AppInfo.Name");
            Description = Tr.Get($"{translation}AppInfo.Description");
            Link = new Uri(Tr.Get($"{translation}AppInfo.Link"));
            Image = Convert.FromBase64String(base64Image);
        }

        public static AppInfo GetAppInfo(AppInfoType heleusAppType)
        {
            if (_appInfo.TryGetValue(heleusAppType, out var appInfo))
                return appInfo;

            switch (heleusAppType)
            {
                case AppInfoType.Heleus:
                    appInfo = new AppInfo("Heleus", "");
                    break;

                case AppInfoType.Message:
                    appInfo = new AppInfo("Message", "");
                    break;

                case AppInfoType.Status:
                    appInfo = new AppInfo("Status", "");
                    break;

                case AppInfoType.Todo:
                    appInfo = new AppInfo("Todo", "");
                    break;

                case AppInfoType.Verify:
                    appInfo = new AppInfo("Verify", "");
                    break;

                case AppInfoType.Note:
                    appInfo = new AppInfo("Note", "");
                    break;
            }

            if (appInfo != null)
                _appInfo[heleusAppType] = appInfo;

            return appInfo;
        }
    }
}
