using System;
using System.IO;
using Xamarin.Forms;

namespace Heleus.Apps.Shared
{
    public static class AccountDummyImage
    {
        static readonly string _imageDataBase64 = "";
        public static readonly byte[] ImageData = Convert.FromBase64String(_imageDataBase64);

        public static ImageSource ImageSource
        {
            get
            {
                return ImageSource.FromStream(() => new MemoryStream(ImageData));
            }
        }
    }
}
